<body>
	<div class="wrap">
		<h4 class="well"><?php echo L('INITIATE_TEAM');?></h4>
		<div class="home_info" id="home_devteam">
			<ul>
				<li><em>华软公益队</em> <a href="http://www.华软公益队.com" target="_blank">www.CXCL工作室.com</a></li>
			<!--	<li><em><?php echo L('TEAM_MEMBERS');?></em> <span></span></li>-->
			</ul>
		</div>
	</div>
	<script src="/2016Sise/public/js/common.js"></script>
	<script>
		//获取官方通知
		$.getJSON("http://www.华软公益队.com/service/sms_jsonp.php?lang=<?php echo (LANG_SET); ?>&v=<?php echo (SIMPLEWIND_CMF_VERSION); ?>&callback=?",
		function(data) {
			var tpl = '<li><em class="title"></em><span class="content"></span></li>';
			var $thinkcmf_notices = $("#thinkcmf_notices");
			$thinkcmf_notices.empty();
			if (data.length > 0) {
				$.each(data, function(i, n) {
					var $tpl = $(tpl);
					$(".title", $tpl).html(n.title);
					$(".content", $tpl).html(n.content);
					$thinkcmf_notices.append($tpl);
				});
			} else {
				$thinkcmf_notices.append("<li>^_^,<?php echo L('NO_NOTICE');?>~~</li>");
			}

		});
	</script>
</body>
</html>